    private void accionConvertirCelciusAFarenheit(){
        int celcius = Integer.parseInt(textCelcius.getText());
        
        ConvertidorGrados convertidor = new ConvertidorGrados();
        int farenheit = convertidor.celciusAFarenheit(celcius);
        resultadoCelcius.setText(celcius + "ยบ Celcius son: " + farenheit +"ยบ Farenheit ");  
    }
            
    private void btnConvertirFACActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConvertirFACActionPerformed
        // TODO add your handling code here:
        accionConvertirFarenheitACelcius();
    }//GEN-LAST:event_btnConvertirFACActionPerformed
    private void accionConvertirFarenheitACelcius(){
        int farenheit = Integer.parseInt(textFarenheit.getText());
        
        ConvertidorGrados convertidor = new ConvertidorGrados();
        int celcius = convertidor.farenheitACelcius(farenheit);
        resultadoFarenheit.setText(farenheit + "ยบ Farenheit son: " + celcius +"ยบ Celcius ");
    }
    
